import React, { useState, useEffect } from 'react';
import TopBar from './TopBar';
import './HomePage.css';

const HomePage = () => {
  const [products, setProducts] = useState([]); // Estado inicial vacío

  useEffect(() => {
    // Función para cargar productos desde la base de datos
    const fetchProducts = async () => {
      try {
        const response = await fetch('/api/products'); // Ajusta esta URL a tu API
        const data = await response.json();
        setProducts(data); // Actualiza el estado con los productos obtenidos
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts(); // Llamar a la función al montar el componente
  }, []); // El array vacío asegura que el efecto se ejecute solo una vez

  return (
    <div className="home-page">
      <TopBar />
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} className="product-image"/>
            <div className="product-info">
              <h3 className="product-name">{product.name}</h3>
              <p className="product-price">{product.price} EUR</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
