import React, { useState, useEffect } from 'react';
import './Stylesheets/home.css';
import TopBar from './Componentes/topBar';
import axios from 'axios';
import { Link } from 'react-router-dom'; 
import { useParams } from 'react-router-dom';


const ProductPage = () => {
    const [product, setProduct] = useState(null);
    const { productId } = useParams(); 

    useEffect(() => {
        const fetchProduct = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/api/producto/${productId}`);
            setProduct(response.data);
            console.log('Producto:', response.data);
        } catch (error) {
            console.error('Error fetching product details:', error);
        }
        };
        fetchProduct();
    }, [productId]);

    if (!product) {
      return <div>ah...</div>; 
    }

    return (
        <div className="product-page">
            <img src={product.Imagen} alt={product.Nombre} className="product-image-detail"/>
            <div className="product-details">
                <h1>{product.Nombre}</h1>
                <p className="product-description">{product.Descripción}</p>
                <p className="product-price">Precio: {product.Precio} EUR</p>
            </div>
        </div>
    );
  };

export default ProductPage;
