import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import { loadStripe } from '@stripe/stripe-js';
import { Elements, CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import TopBar from './Componentes/topBar';
import './Stylesheets/pasarela.css'; // Asegúrate de crear y enlazar tu archivo CSS

const stripePromise = loadStripe('your_public_key'); // Reemplaza 'your_public_key' con tu clave pública de Stripe

const CheckoutForm = ({ product }) => {
  const stripe = useStripe();
  const elements = useElements();
  const [message, setMessage] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const history = useHistory();

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsProcessing(true);

    const { error, paymentMethod } = await stripe.createPaymentMethod({
        type: "card",
        card: elements.getElement(CardElement),
    });
    setLoading(true);

    if (!error) {
    // console.log(paymentMethod)
    const { id } = paymentMethod;
    try {
        const { data } = await axios.post("http://localhost:5000/api/payment/checkout",{
            id,
            amount: product.Precio*100, }
        );
        console.log(data);

        elements.getElement(CardElement).clear();
    } catch (error) {
        console.log(error);
    }
    setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="checkout-form">
      <div className="product-card">
        <img src={product.Imagen} alt={product.Nombre} className="product-image" />
        <div className="product-info">
          <h2>{product.Nombre}</h2>
          <span className="product-price">{product.Precio} €</span>
          <p className="product-description">{product.Descripcion}</p>
        </div>
      </div>
      <CardElement className="card-element" />
      <button disabled={!stripe || setLoading}>
        {setLoading ? 'Procesando...' : 'Pagar'}
      </button>
      {message && <div>{message}</div>}
    </form>
  );
};

const Pasarela = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/producto/${productId}`);
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };
    fetchProduct();
  }, [productId]);

  if (!product) {
    return <div>Cargando...</div>;
  }

  return (
    <div>
      <TopBar />
      <div className="pasarela-page">
        <div className="checkout-form-container">
          <Elements stripe={stripePromise}>
            <CheckoutForm product={product} />
          </Elements>
        </div>
      </div>
    </div>
  );
};

export default Pasarela;
