import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Stylesheets/userProfile.css'; 
import TopBar from './Componentes/TopBar';

const UserProfile = () => {
  const [userInfo, setUserInfo] = useState({
    Nombre: '',
    Apellidos: '',
    Direccion: '',
    Correo: '',
    Productos: []
  });
  const [mensaje, setMensaje] = useState('');

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/usuario/info', { withCredentials: true });
        setUserInfo(response.data);
      } catch (error) {
        console.error('Error al obtener la información del usuario:', error);
      }
    };

    fetchUserInfo();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put('http://localhost:5000/api/usuario/info', userInfo, { withCredentials: true });
      setUserInfo(response.data);
      setMensaje('Información actualizada exitosamente');
    } catch (error) {
      console.error('Error al actualizar la información del usuario:', error);
      setMensaje('Hubo un problema al actualizar la información');
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/api/usuario/auth/logout', {}, { withCredentials: true });
      window.location.href = '/login';
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
  };

  return (
    <div>
      <TopBar />
      <div className="user-profile">
        <h2>Perfil del Usuario</h2>
        <form onSubmit={handleUpdate} className="user-profile-form">
          <div className="form-group">
            <label>Nombre</label>
            <input
              type="text"
              name="Nombre"
              value={userInfo.Nombre}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Apellidos</label>
            <input
              type="text"
              name="Apellidos"
              value={userInfo.Apellidos}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Dirección</label>
            <input
              type="text"
              name="Direccion"
              value={userInfo.Direccion}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Correo</label>
            <input
              type="email"
              name="Correo"
              value={userInfo.Correo}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Actualizar Información</button>
        </form>
        {mensaje && <p>{mensaje}</p>}
        <button onClick={handleLogout} className="logout-button">Cerrar Sesión</button>
        <h3>Productos en Venta</h3>
        <ul className="product-list">
          {userInfo.Productos.map(product => (
            <li key={product._id}>
              {product.Nombre} - {product.Precio} EUR
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default UserProfile;
