import React, { useState } from 'react';
import axios from 'axios';
import './Stylesheets/sellProduct.css';
import TopBar from './Componentes/TopBar';

const SellProductPage = () => {
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [precio, setPrecio] = useState('');
  const [imagen, setImagen] = useState(null);
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('Nombre', nombre);
    formData.append('Descripcion', descripcion);
    formData.append('Precio', precio);
    formData.append('Imagen', imagen);

    try {
      await axios.post('http://localhost:5000/api/producto/create', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setMessage('Producto creado exitosamente');
      setNombre('');
      setDescripcion('');
      setPrecio('');
      setImagen(null);
    } catch (error) {
      console.error('Error al crear el producto:', error);
      setMessage('Error al crear el producto');
    }
  };

  return (
    <div>
      <TopBar />
      <div className="sell-product-page">
        <div className="sell-product-container">
          <h2>Poner un Producto a la Venta</h2>
          <form onSubmit={handleSubmit} className="sell-product-form">
            <div className="form-group">
              <label>Nombre del Producto</label>
              <input
                type="text"
                value={nombre}
                onChange={(e) => setNombre(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Descripci√≥n</label>
              <textarea
                value={descripcion}
                onChange={(e) => setDescripcion(e.target.value)}
                required
              ></textarea>
            </div>
            <div className="form-group">
              <label>Precio</label>
              <input
                type="number"
                value={precio}
                onChange={(e) => setPrecio(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label>Imagen</label>
              <input
                type="file"
                onChange={(e) => setImagen(e.target.files[0])}
                required
              />
            </div>
            <button type="submit">Vender Producto</button>
          </form>
          {message && <p>{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default SellProductPage;
