import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import TopBar from './Componentes/topBar';
import './Stylesheets/product.css'; // Asegúrate de crear o ajustar este archivo CSS

const ProductPage = () => {
    const [product, setProduct] = useState(null);
    const { id } = useParams();  // Asume que el parámetro se llama 'id'
    const user = { name: 'Nombre del Usuario', userId: '12345' }; // Simula datos de usuario

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/producto/${id}`);
                setProduct(response.data);
            } catch (error) {
                console.error('Error fetching product details:', error);
            }
        };

        fetchProduct();
    }, [id]);

    if (!product) {
        return <div>Loading...</div>;
    }

    // Funciones para manejar favoritos y compras
    const addToFavorites = () => {
        console.log('Agregar a favoritos:', product._id);
        // Implementar lógica para agregar a favoritos
    };

    const buyProduct = () => {
        console.log('Comprar producto:', product._id);
        // Implementar lógica de compra
    };

    return (
        <div className="product-page">
            <TopBar />
            <h2><Link to={`/profile/${user.userId}`}>{user.name}</Link></h2>
            <div className="product-container">
                <img src={product.Imagen} alt={product.Nombre} className="product-image-detail"/>
                <div className="product-details">
                    <h1>{product.Nombre}</h1>
                    <p className="product-price">Precio: {product.Precio} EUR</p>
                    <p className="product-description">{product.Descripción}</p>
                    <p className="product-categories">Categorías: {product.Categorias.join(', ')}</p>
                    <button onClick={addToFavorites}>Añadir a Favoritos</button>
                    <button onClick={buyProduct}>Comprar</button>
                </div>
            </div>
        </div>
    );
};

export default ProductPage;
