import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; 

const LoginPage = () => {
  const [correo, setCorreo] = useState('');
  const [contraseña, setContraseña] = useState('');
  const [mostrarContraseña, setMostrarContraseña] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        correo,
        contraseña
      });
      console.log('Inicio de sesión exitoso:', response.data);
      setMensaje('Inicio de sesión exitoso');
      // Aquí podrías redirigir al usuario o guardar el token de sesión, etc.
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
      setMensaje('Hubo un problema al iniciar sesión, verifica tus credenciales');
    }
  };

  return (
    <div className="login-container">
      <h1>Iniciar Sesión</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="correo">Correo</label>
        <input
          id="correo"
          type="email"
          value={correo}
          onChange={(e) => setCorreo(e.target.value)}
          required
        />

        <label htmlFor="contraseña">Contraseña</label>
        <input
          id="contraseña"
          type={mostrarContraseña ? "text" : "password"}
          value={contraseña}
          onChange={(e) => setContraseña(e.target.value)}
          required
        />

        <label>
          <input
            type="checkbox"
            checked={mostrarContraseña}
            onChange={(e) => setMostrarContraseña(e.target.checked)}
          />
          Mostrar contraseña
        </label>

        <div className="actions">
          <button type="submit">Iniciar Sesión</button>
          <button type="button" onClick={() => {/* Lógica para volver a la página de inicio */}}>
            Cancelar
          </button>
        </div>

        <div className="links">
          <a href="/recordar-contraseña">¿Olvidaste tu contraseña?</a>
          <a href="/crear-cuenta">Crear una cuenta</a>
        </div>
      </form>
    </div>
  );
};

export default LoginPage;
