import React, { useState, useEffect } from 'react';
import './Stylesheets/product.css'; // Asegúrate de incluir y enlazar correctamente tu CSS
import { useParams } from 'react-router-dom';
import axios from 'axios';
import TopBar from './Componentes/topBar';
import { loadStripe } from '@stripe/stripe-js';
import { Elements } from '@stripe/react-stripe-js';

const stripePromise = loadStripe('your_public_key'); // Reemplaza 'your_public_key' con tu clave pública de Stripe

const ProductPage = () => {
    const [product, setProduct] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/producto/${id}`);
                setProduct(response.data);
            } catch (error) {
                console.error('Error fetching product details:', error);
            }
        };
        fetchProduct();
    }, [id]);

    const handleBuy = async () => {
        try {
            const response = await axios.post('http://localhost:5000/api/stripe/create-checkout-session', {
                price: product.Precio,
                productId: product._id
            });
            const { id } = response.data;
            const stripe = await stripePromise;
            await stripe.redirectToCheckout({ sessionId: id });
        } catch (error) {
            console.error('Error creating checkout session:', error);
        }
    };

    if (!product) {
        return <div>Cargando...</div>;
    }

    return (
        <Elements stripe={stripePromise}>
            <div>
                <TopBar />
                <div className="product-page">
                    <div className="product-card">
                        <img src={product.Imagen} alt={product.Nombre} className="product-image"/>
                        <div className="product-info">
                            <h2>{product.Nombre}</h2>
                            <span className="product-price">{product.Precio} €</span>
                            <p className="product-description">{product.Descripcion}</p>
                            <div className="product-actions">
                                <button className="btn-favorite">❤ Añadir a Favoritos</button>
                                <button className="btn-buy" onClick={handleBuy}>Comprar</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Elements>
    );
};

export default ProductPage;
