import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../Stylesheets/topBar.css'; 
import logo from '../images/logo5.png';

const TopBar = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const sessionResponse = await axios.get('http://localhost:5000/api/usuario/auth/session', { withCredentials: true });
        if (sessionResponse.status === 200) {
          const userId = sessionResponse.data._id.toString();
          console.log(sessionResponse);
          const userResponse = await axios.get(`http://localhost:5000/api/usuario/${userId}`, { withCredentials: true });
          setUser(userResponse.data);
          console.log('Información del usuario:', userResponse.data);
        }
      } catch (error) {
        setUser(null);
      }
    };

    fetchUserInfo();
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:5000/api/usuario/auth/logout', {}, { withCredentials: true });
      window.location.href = '/';
    } catch (error) {
      console.error('Error al cerrar sesión:', error);
    }
  };

  return (
    <div className="top-bar">
      {/* Logo */}
      <div className="logo" onClick={() => { window.location.href = '/'; }}>
        <img src={logo} alt="Swapster Logo" className="nav-logo" />
      </div>

      {/* Search Bar */}
      <div className="search-bar">
        <input type="text" placeholder="Buscar" />
      </div>

      {/* Session/Login or Profile Button */}
      <div className="session-button">
        {!user ? (
          <>
            <button onClick={() => { window.location.href = '/login'; }}>
              Regístrate o inicia sesión
            </button>
            <button onClick={() => { window.location.href = '/vender'; }}>
              + Vender
            </button>
          </>
        ) : user.Rol === 'user' ? (
          <>
            <button onClick={() => { window.location.href = '/perfil'; }}>
              Perfil
            </button>
            <button onClick={() => { window.location.href = '/vender'; }}>
              + Vender
            </button>
          </>
        ) : user.Rol === 'admin' ? (
          <>
            <button onClick={() => { window.location.href = '/productos'; }}>
              Ver Productos
            </button>
            <button onClick={() => { window.location.href = '/usuarios'; }}>
              Ver Usuarios
            </button>
            <button onClick={handleLogout}>
              Cerrar Sesión
            </button>
          </>
        ) : null}
      </div>
    </div>
  );
};

export default TopBar;
